// Sistema de Solicitação de Peças - Página de Envio da Solicitação

document.addEventListener('DOMContentLoaded', function() {
    // O app.js já verifica o login, então podemos carregar os detalhes diretamente.
    carregarDetalhesSolicitacao();

    document.getElementById('form-envio').addEventListener('submit', function(e) {
        e.preventDefault();
        salvarEnvio();
    });

    document.getElementById('btn-imprimir').addEventListener('click', function() {
        gerarPDF();
    });
});

// Função para carregar os detalhes da solicitação
async function carregarDetalhesSolicitacao() {
    const id = getQueryParam('id');
    if (!id) {
        alert('ID da solicitação não fornecido.');
        window.close();
        return;
    }

    // Carregar usuário logado
    const { data, error } = await supabase.auth.getUser();
    let currentUser = null;
    if (data && !error) {
        currentUser = data.user;
        document.getElementById('usuario-logado').value = currentUser.user_metadata?.email || currentUser.user_metadata?.name || 'Usuário não identificado';
    } else {
        document.getElementById('usuario-logado').value = 'Não logado';
    }

    const { data: solicitacao, error: solicitacaoError } = await supabase
        .from('solicitacoes')
        .select(`
            id, created_at, status, itens, rota, data_envio, enviado_por_id,
            usuario:usuario_id ( nome ),
            veiculo:veiculo_id ( placa, supervisor:supervisor_id(nome) ),
            enviado_por:enviado_por_id ( nome )
        `)
        .eq('id', id)
        .single();

    if (solicitacaoError || !solicitacao) {
        console.error('Erro ao buscar solicitação para envio:', solicitacaoError);
        alert(`Solicitação com ID ${id} não encontrada.`);
        window.close();
        return;
    }

    // Preencher campos do formulário
    document.getElementById('codigo-solicitacao').value = String(solicitacao.id).padStart(5, '0');
    document.getElementById('nome-tecnico').value = solicitacao.usuario?.nome || 'N/A';
    document.getElementById('placa').value = solicitacao.veiculo?.placa || 'N/A';
    document.getElementById('status').value = solicitacao.status;

    // Preencher data e hora (usando data atual se não existir)
    const dataHoraObj = new Date(solicitacao.created_at);
    const dataHoraFormatada = dataHoraObj.toLocaleString('pt-BR', { day: '2-digit', month: '2-digit', year: 'numeric', hour: '2-digit', minute: '2-digit' });
    document.getElementById('data-hora').value = dataHoraFormatada.replace(',', '');

    // Preencher rota (usando valor padrão se não existir)
    const rota = solicitacao.rota || 'Não definida';
    document.getElementById('rota').value = rota;

    // Preencher data e hora de envio com os valores atuais do computador
    const agora = new Date();
    const dataAtual = agora.toISOString().split('T')[0]; // Formato YYYY-MM-DD
    const horaAtual = agora.toTimeString().slice(0, 5); // Formato HH:MM

    // Se já foi enviado, preenche com os dados salvos e desabilita os campos
    if (solicitacao.data_envio) {
        const dataEnvioObj = new Date(solicitacao.data_envio);
        document.getElementById('data-envio').value = dataEnvioObj.toISOString().split('T')[0];
        document.getElementById('hora-envio').value = dataEnvioObj.toTimeString().slice(0, 5);
    } else {
        document.getElementById('data-envio').value = dataAtual;
        document.getElementById('hora-envio').value = horaAtual;
    }

    // Preencher grid de itens
    const itensGrid = document.getElementById('itens-grid');
    itensGrid.innerHTML = '';
    const sectionHeader = itensGrid.parentElement.querySelector('.section-header');
    const oldTotal = sectionHeader.querySelector('.total-pecas-label');
    if(oldTotal) oldTotal.remove();

    if (solicitacao.itens && solicitacao.itens.length > 0) {
        // Calcular total de peças
        const totalQuantidade = solicitacao.itens.reduce((total, item) => total + item.quantidade, 0);

        // Criar e inserir o totalizador no cabeçalho da seção
        const totalLabel = document.createElement('div');
        totalLabel.className = 'total-pecas-label';
        totalLabel.innerHTML = `Total de Peças: <strong>${totalQuantidade}</strong>`;
        sectionHeader.appendChild(totalLabel);

        // Criar e preencher a tabela de itens
        const tabela = document.createElement('table');
        tabela.innerHTML = `
            <thead>
                <tr><th>Código</th><th>Nome da Peça</th><th>Quantidade</th></tr>
            </thead>
            <tbody>
                ${solicitacao.itens.map(item => `<tr><td>${item.codigo}</td><td>${item.nome}</td><td>${item.quantidade}</td></tr>`).join('')}
            </tbody>
        `;
        itensGrid.appendChild(tabela);
    }

    // Salvar ID da solicitação no formulário para uso posterior
    const form = document.getElementById('form-envio');
    form.dataset.solicitacaoId = id;

    // Se a solicitação já foi enviada, desabilitar o botão de salvar
    if (solicitacao.status === 'enviado') {
        document.getElementById('btn-enviar').disabled = true;
        document.getElementById('btn-enviar').textContent = 'Já Enviado';
        document.getElementById('data-envio').readOnly = true;
        document.getElementById('hora-envio').readOnly = true;
    }
}

// Função para salvar o envio
async function salvarEnvio() {
    const form = document.getElementById('form-envio');
    const id = form.dataset.solicitacaoId;

    if (!id) {
        alert('Nenhuma solicitação selecionada.');
        return;
    }

    // Pega a data e hora exatas do momento do clique, ignorando os campos do formulário.
    const dataHoraEnvio = new Date();

    const { error } = await supabase
        .from('solicitacoes')
        .update({
            status: 'enviado',
            data_envio: dataHoraEnvio.toISOString(),
            updated_at: new Date().toISOString()
        })
        .eq('id', id);

    if (error) {
        console.error('Erro ao marcar como enviado:', error);
        alert('Erro ao salvar o envio da solicitação.');
        return;
    }
    alert('Solicitação marcada como enviada com sucesso!');

    // Tenta recarregar a página que abriu esta (aprovados.html)
    if (window.opener) {
        window.opener.location.reload();
    }

    // Fechar a aba após ação
    window.close();
}

// Função para gerar PDF
async function gerarPDF() {
    const form = document.getElementById('form-envio');
    const id = form.dataset.solicitacaoId;

    if (!id) {
        alert('Nenhuma solicitação selecionada.');
        return;
    }

    const { data: solicitacao, error } = await supabase
        .from('solicitacoes')
        .select(`*, usuario:usuario_id(nome), veiculo:veiculo_id(placa, supervisor:supervisor_id(nome))`)
        .eq('id', id)
        .single();

    if (error || !solicitacao) {
        alert('Não foi possível carregar os dados para gerar o PDF.');
        return;
    }

    const { jsPDF } = window.jspdf;
    const doc = new jsPDF();

    // --- CABEÇALHO ---

    // IMPORTANTE: Para usar seu logo 'logo.png', converta-o para o formato Base64.
    // 1. Acesse um conversor online como: https://www.base64-image.de/
    // 2. Envie seu arquivo 'logo.png'.
    // 3. Copie o texto gerado e cole-o dentro das aspas da variável 'logoBase64' abaixo.
    const logoBase64 = 'data:image/png;base64,'; // Substitua este conteúdo
    const placeholderLogo = 'url(data:image/png;base64,)';

    // Adiciona a logo apenas se não for o placeholder, tratando possíveis erros.
    if (logoBase64 && logoBase64 !== placeholderLogo) {
        try {
            doc.addImage(logoBase64, 'PNG', 150, 8, 45, 15);
        } catch (e) {
            console.warn('Não foi possível adicionar o logo ao PDF. Verifique se o código Base64 está correto.', e);
            alert('Aviso: O logo não pôde ser carregado, mas o PDF foi gerado mesmo assim.');
        }
    } else {
        console.log('Logo placeholder detectado. Pulando a adição do logo no PDF. Substitua o conteúdo da variável "logoBase64" para exibir o logo da sua empresa.');
    }

    // Título do Documento dinâmico com base no status
    let tituloPDF = 'Relatório de Solicitação de Peças'; // Título padrão
    switch (solicitacao.status) {
        case 'enviado':
            tituloPDF = 'Resumo de Envio de Peças';
            break;
        case 'rejeitado':
            tituloPDF = 'Rejeitado o Envio de Peças';
            break;
        case 'aprovado':
            tituloPDF = 'Pendente Envio de Peças';
            break;
    }
    // Título do Documento
    doc.setFontSize(20);
    doc.setTextColor('#4CAF50');
    doc.setFont('helvetica', 'bold');
    doc.text(tituloPDF, 14, 20);

    // Linha divisória
    doc.setDrawColor(76, 175, 80);
    doc.setLineWidth(0.5);
    doc.line(14, 25, 196, 25);

    // --- INFORMAÇÕES GERAIS ---
    const labelCol1 = 58;
    const valueCol1 = 60;
    const labelCol2 = 143;
    const valueCol2 = 145;

    doc.setFontSize(12);
    doc.setTextColor(40);

    let startY = 40;

    // Helper para desenhar campos alinhados
    const drawField = (label, value, y, isSecondColumn = false) => {
        const labelX = isSecondColumn ? labelCol2 : labelCol1;
        const valueX = isSecondColumn ? valueCol2 : valueCol1;
        doc.setFont('helvetica', 'bold');
        doc.text(label, labelX, y, { align: 'right' });
        doc.setFont('helvetica', 'normal');
        doc.text(String(value), valueX, y);
    };

    const dataHora = new Date(solicitacao.created_at).toLocaleString('pt-BR');
    drawField('Código da Solicitação:', String(solicitacao.id).padStart(5, '0'), startY);
    drawField('Data da Solicitação:', dataHora, startY, true);

    startY += 8;
    drawField('Técnico:', solicitacao.usuario?.nome || 'N/A', startY);

    startY += 8;
    drawField('Placa do Veículo:', solicitacao.veiculo?.placa || 'N/A', startY);
    drawField('Supervisor:', solicitacao.veiculo?.supervisor?.nome || 'N/A', startY, true);

    startY += 8;
    drawField('Rota de Entrega:', solicitacao.rota || 'Não definida', startY);

    startY += 8;
    const dataEnvio = solicitacao.data_envio ? new Date(solicitacao.data_envio).toLocaleString('pt-BR') : 'Aguardando envio';
    drawField('Data de Envio:', dataEnvio, startY);

    // --- TABELA DE ITENS ---

    // Adicionar total de peças antes da tabela
    const totalQuantidadePDF = solicitacao.itens.reduce((total, item) => total + item.quantidade, 0);
    doc.setFont('helvetica', 'bold');
    doc.text(`Total de Peças: ${totalQuantidadePDF}`, 196, startY + 10, { align: 'right' });

    const tableColumn = ["Código", "Nome da Peça", "Quantidade"];
    const tableRows = [];
    solicitacao.itens.forEach(item => {
        tableRows.push([item.codigo, item.nome, item.quantidade]);
    });

    doc.autoTable({
        head: [tableColumn],
        body: tableRows,
        startY: startY + 15,
        theme: 'grid',
        headStyles: { fillColor: [76, 175, 80] },
        styles: { font: 'helvetica', fontSize: 10 }
    });

    // --- RODAPÉ ---
    const pageCount = doc.internal.getNumberOfPages();
    for (let i = 1; i <= pageCount; i++) {
        doc.setPage(i);
        doc.setFontSize(8);
        doc.setTextColor(150);
        doc.text(`Documento gerado em: ${new Date().toLocaleString('pt-BR')}`, 14, 287);
        doc.text(`Página ${i} de ${pageCount}`, 196, 287, { align: 'right' });
    }

    // Salvar o PDF
    doc.save(`solicitacao_${String(solicitacao.id).padStart(5, '0')}.pdf`);
}
